```type Node interface {
	String() string
}

type Object struct {
	Elements map[string]Node
}

func (o Object) String() string {
	var out bytes.Buffer
	out.WriteString("{")

	elements := []string{}
	for name, node := range o.Elements {
		buf := name + ":" + node.String()
		elements = append(elements, buf)
	}

	out.WriteString(strings.Join(elements, ","))
	out.WriteString("}")
	return out.String()
}```

```package ast

import (
	"testing"

	"<http://github.com/skanehira/go-json/token|github.com/skanehira/go-json/token>"
)

func TestString(t *testing.T) {
	want := `"a"`
	str := String{
		Value: "a",
	}

	if str.String() != want {
		t.Fatalf("want=%s, got=%s", want, str.String())
	}
}

func TestObject(t *testing.T) {
	o := Object{
		Elements: map[string]Node{
			"name": String{
				Type:  token.String,
				Value: "gorilla",
			},
		},
	}

	want := `{"name":"gorilla"}`

	if o.String() != want {
		t.Fatalf("want=%s, got=%s", want, o.String())
	}
}```

```type Node interface {
	String() string
	TokenType() token.TokenType
}

type Object struct {
	Elements map[string]Node
	Type     token.TokenType
}

func (o Object) String() string {
	var out strings.Builder
	out.WriteString("{")

	elements := []string{}
	for name, node := range o.Elements {
		e := "\"" + name + "\""
		if node.TokenType() == token.String {
			e += ":" + node.String()
		}
		elements = append(elements, e)
	}

	out.WriteString(strings.Join(elements, ","))
	out.WriteString("}")
	return out.String()
}

func (o Object) TokenType() token.TokenType {
	return o.Type
}

type String struct {
	Value string
	Type  token.TokenType
}

func (s String) String() string {
	return "\"" + s.Value + "\""
}

func (s String) TokenType() token.TokenType {
	return s.Type
}```
