package main

import (
	"fmt"

	json "<http://github.com/skanehira/json-unmarshaller|github.com/skanehira/json-unmarshaller>"
)

func main() {
	var s struct {
		Name      string   `json:"name"`
		Age       int      `json:"age"`
		IsHuman   bool     `json:"is_human"`
		Languages []string `json:"languages"`
	}

	data := `
{
	"name": "gorilla,
	"age": 26,
	"is_human": false,
	"languages": [
		"Go",
		"PHP",
		"Java",
		"Java Script",
		"HTML"
	]
}`

	b := []byte(data)

	if err := json.Unmarshal(b, &amp;s); err != nil {
		panic(err)
	}

	fmt.Printf("%#+v", s)
}
