package main

import (
	"fmt"
)

func main() {
	a := []string{}
	b := []string{"foo", "bar"}

	a = append(a, b...)
	fmt.Println(a) // [foo bar]

	a = []string{}
	b = []string{"foo"}

	a = append(a, b...)
	fmt.Println(a) // [foo]

	a = []string{}
	b = []string{}

	a = append(a, b...)
	fmt.Println(a) // []

	// i.e. empty slice with ... mean zero argument.

	a = []string{}
	b = []string{}

	a = append(a)
	fmt.Println(a) // []
}
