```package engine
type Callbacker interface {
    Callback(string)
}
type Engine struct {
    cb Callbacker
}
func New(cb Callbacker) &amp;Engine {
    return &amp;Engine{cb: cb}
}
func (e *Engine) DoSomething() {
    // Do something and call backback
    e.cb.Callback("done")
