mapと2重forのベンチマークのとり方、これで良いですかね（不安）

```package main

import "testing"

func setup() []int {
	slice := make([]int, 1000)
	slice[999] = 1
	return slice
}

func BenchmarkInSlice(b *testing.B) {
	slice := setup()
	b.ResetTimer()
	for i := 0; i &lt; b.N; i++ {
		for _, s := range slice {
			if s == 1 {
				return
			}
		}
	}
}

func BenchmarkInMap(b *testing.B) {
	slice := setup()
	b.ResetTimer()
	for i := 0; i &lt; b.N; i++ {
		m := map[int]struct{}{}
		for _, s := range slice {
			m[s] = struct{}{}
		}

		if _, ok := m[1]; ok {
			return
		}
	}
}```
