```ifeq ($(OS),Windows_NT)
EXT = .exe
endif

BINS = $(subst cmd/,bin/,$(wildcard cmd/*))

all: $(BINS)
	@echo done

bin/%: cmd/%/main.go
	@go build -ldflags "$(LDFLAGS)" -o $@$(EXT) $&lt;

clean:
	-@rm -f $(BINS)```

