function! SmArTcAsE(s1, s2) abort
  let l:cc1 = split(a:s1, '\zs')
  let l:cc2 = split(a:s2, '\zs')
  for l:i in range(min([len(l:cc1), len(l:cc2)]))
    let up1 = l:cc1[l:i] ==# toupper(l:cc1[l:i])
    let up2 = l:cc2[l:i] ==# toupper(l:cc2[l:i])
    if up1 &gt; up2
      let l:cc2[l:i] = toupper(l:cc2[l:i])
    elseif up1 &lt; up2
      let l:cc2[l:i] = tolower(l:cc2[l:i])
    endif
  endfor
  return join(l:cc2, '')
endfunction
