function! s:is_most_left(nr)
  let l:oldw = winnr()
  silent! exe "normal! \&lt;c-w&gt;h"
  let l:neww = winnr()
  silent! exe l:oldw.'wincmd w'
  return l:oldw == l:neww
endfunction

function! s:is_most_right(nr)
  let l:oldw = winnr()
  silent! exe "normal! \&lt;c-w&gt;l"
  let l:neww = winnr()
  silent! exe l:oldw.'wincmd w'
  return l:oldw == l:neww
endfunction

function! s:wider(c)
  let l:w = winnr()
  if s:is_most_left(l:w)
    exe 'normal! ' . a:c . "\&lt;c-w&gt;&lt;"
  elseif s:is_most_right(l:w)
    exe 'normal! ' . a:c . "\&lt;c-w&gt;&gt;"
  endif
endfunction

nnoremap &lt;plug&gt;(wider) :&lt;c-u&gt;call &lt;SID&gt;wider(v:count)&lt;cr&gt;
map &lt;c-w&gt;W &lt;plug&gt;(wider)
